mapper.select:
select count(*)
from tbl_user


        name like '%' #{name} '%'


        and deptno like  '%' #{deptno} '%'


        and lockState = #{lockState}


        and expireTime > #{expireTimeF}


        and expireTime < #{expireTimeB}

select id, name, loginAct, deptno, email, expireTime, allowIps, lockState, createBy, createTime, editBy, editTime from tbl_user
name like '%' #{name} '%'
and deptno like '%' #{deptno} '%'
and lockState = #{lockState}
and expireTime > #{expireTimeF}
and expireTime < #{expireTimeB}
order by createTime desc limit #{skipCount},#{pageSize}
